const About = () => {
  return (
    <section style={{ padding: '4rem 2rem', maxWidth: '700px', margin: '0 auto' }}>
      <h2 style={{ fontSize: '2rem', marginBottom: '1rem' }}>About Me</h2>
      <p>
        Passionate about crafting smooth mobile experiences with Swift and building beautiful,
        responsive web interfaces using React. I enjoy turning complex ideas into elegant, functional products.
      </p>
    </section>
  );
};

export default About;
